package com.example.colours.services.Impl;import com.example.colours.services.BluePrinter;import com.example.colours.services.ColourPrinter;import com.example.colours.services.GreenPrinter;import com.example.colours.services.RedPrinter;public class ColourPrinterImpl implements ColourPrinter {    private final RedPrinter redPrinter;    private final GreenPrinter greenPrinter;    private final BluePrinter bluePrinter;    public ColourPrinterImpl(RedPrinter redPrinter, GreenPrinter greenPrinter, BluePrinter bluePrinter) {        this.redPrinter = redPrinter;        this.greenPrinter = greenPrinter;        this.bluePrinter = bluePrinter;    }    @Override    public String print(){        return String.join(", ",redPrinter.print(),bluePrinter.print(),greenPrinter.print());    }}